import React from 'react';
import { Box, Container, Typography, Grid, Card, CardContent, CardMedia, Button } from '@mui/material';
import { styled } from '@mui/material/styles';

const MenuContainer = styled(Container)(({ theme }) => ({
  padding: theme.spacing(8, 0),
  position: 'relative',
  '&::before': {
    content: '""',
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    height: '4px',
    background: `repeating-linear-gradient(
      45deg,
      ${theme.palette.primary.main},
      ${theme.palette.primary.main} 10px,
      ${theme.palette.secondary.main} 10px,
      ${theme.palette.secondary.main} 20px
    )`,
  }
}));

const MenuItemCard = styled(Card)(({ theme }) => ({
  height: '100%',
  display: 'flex',
  flexDirection: 'column',
  borderRadius: '15px',
  overflow: 'hidden',
  boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
  transition: 'transform 0.3s ease, box-shadow 0.3s ease',
  '&:hover': {
    transform: 'translateY(-5px)',
    boxShadow: '0 6px 12px rgba(0,0,0,0.15)',
  },
  '& .MuiCardMedia-root': {
    height: 200,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
  },
  '& .MuiCardContent-root': {
    flexGrow: 1,
    padding: theme.spacing(3),
    background: theme.palette.background.paper,
  }
}));

const menuItems = [
  {
    title: 'Signature Cupcakes',
    description: 'Our famous handcrafted cupcakes with unique flavors and decorations',
    image: 'https://placehold.co/400x300/FFF5E6/8B4513?text=🍰',
    price: '$4.50'
  },
  {
    title: 'Artisan Bread',
    description: 'Freshly baked bread made with organic ingredients',
    image: 'https://placehold.co/400x300/FFF5E6/8B4513?text=🍞',
    price: '$6.99'
  },
  {
    title: 'Seasonal Pies',
    description: 'Handmade pies with seasonal fruits and buttery crust',
    image: 'https://placehold.co/400x300/FFF5E6/8B4513?text=🥧',
    price: '$8.99'
  },
  {
    title: 'Custom Cakes',
    description: 'Custom-designed cakes for any special occasion',
    image: 'https://placehold.co/400x300/FFF5E6/8B4513?text=🎂',
    price: 'From $35'
  }
];

const MenuPreview: React.FC = () => {
  return (
    <MenuContainer>
      <Typography
        variant="h2"
        component="h2"
        align="center"
        sx={{
          fontFamily: '"Caveat", cursive',
          fontSize: '3rem',
          color: 'primary.main',
          marginBottom: 4,
          textShadow: '2px 2px 4px rgba(0,0,0,0.1)',
        }}
      >
        What We Offer
      </Typography>
      <Grid container spacing={4}>
        {menuItems.map((item, index) => (
          <Grid item xs={12} sm={6} md={3} key={index}>
            <MenuItemCard>
              <CardMedia
                component="img"
                image={item.image}
                alt={item.title}
              />
              <CardContent>
                <Typography
                  variant="h5"
                  component="h3"
                  sx={{
                    fontFamily: '"Caveat", cursive',
                    fontSize: '1.8rem',
                    color: 'primary.main',
                    marginBottom: 1,
                  }}
                >
                  {item.title}
                </Typography>
                <Typography
                  variant="body1"
                  color="text.secondary"
                  sx={{ marginBottom: 2 }}
                >
                  {item.description}
                </Typography>
                <Typography
                  variant="h6"
                  color="secondary.main"
                  sx={{
                    fontFamily: '"Caveat", cursive',
                    fontSize: '1.4rem',
                  }}
                >
                  {item.price}
                </Typography>
              </CardContent>
            </MenuItemCard>
          </Grid>
        ))}
      </Grid>
      <Box sx={{ textAlign: 'center', marginTop: 4 }}>
        <Button
          variant="outlined"
          color="primary"
          size="large"
          sx={{
            fontFamily: '"Caveat", cursive',
            fontSize: '1.5rem',
            padding: '10px 30px',
            borderRadius: '30px',
            borderWidth: '2px',
            '&:hover': {
              borderWidth: '2px',
            },
          }}
        >
          View Full Menu
        </Button>
      </Box>
    </MenuContainer>
  );
};

export default MenuPreview;
