import React from 'react';
import { AppBar, Toolbar, Typography, Button, Box } from '@mui/material';
import { styled } from '@mui/material/styles';
import { Link, useLocation } from 'react-router-dom';

const StyledAppBar = styled(AppBar)(({ theme }) => ({
  backgroundColor: 'rgba(255, 248, 220, 0.95)',
  boxShadow: 'none',
  borderBottom: '2px solid #DEB887',
}));

const Logo = styled(Typography)(({ theme }) => ({
  fontFamily: '"Caveat", cursive',
  fontSize: '2.5rem',
  color: theme.palette.primary.main,
  textAlign: 'center',
  flexGrow: 1,
  '&::before, &::after': {
    content: '""',
    display: 'inline-block',
    width: '30px',
    height: '30px',
    backgroundImage: 'url("/wheat-icon.png")',
    backgroundSize: 'contain',
    backgroundRepeat: 'no-repeat',
    margin: '0 15px',
    verticalAlign: 'middle',
  },
}));

const NavButton = styled(Button)<{ component?: React.ElementType }>(({ theme }) => ({
  color: theme.palette.primary.main,
  fontFamily: '"Quicksand", sans-serif',
  fontSize: '1.1rem',
  margin: '0 10px',
  textDecoration: 'none',
  '&:hover': {
    backgroundColor: 'rgba(139, 69, 19, 0.1)',
  },
}));

const Header: React.FC = () => {
  const location = useLocation();

  return (
    <StyledAppBar position="sticky">
      <Toolbar>
        <Logo variant="h1">
          Sweets You Knead
        </Logo>
        <Box sx={{ display: 'flex', gap: 2 }}>
          <NavButton 
            component={location.pathname === '/' ? 'a' : Link} 
            to={location.pathname === '/' ? '#home' : '/'}
            href={location.pathname === '/' ? '#home' : undefined}
          >
            Home
          </NavButton>
          <NavButton 
            component={Link} 
            to="/menu"
          >
            Menu
          </NavButton>
          <NavButton 
            component={location.pathname === '/' ? 'a' : Link} 
            to={location.pathname === '/' ? '#our-story' : '/'}
            href={location.pathname === '/' ? '#our-story' : undefined}
          >
            Our Story
          </NavButton>
          <NavButton 
            component={location.pathname === '/' ? 'a' : Link} 
            to={location.pathname === '/' ? '#contact' : '/'}
            href={location.pathname === '/' ? '#contact' : undefined}
          >
            Contact
          </NavButton>
        </Box>
      </Toolbar>
    </StyledAppBar>
  );
};

export default Header;
