import React from 'react';
import { Box, Typography } from '@mui/material';
import { styled } from '@mui/material/styles';
import { motion } from 'framer-motion';

const HeroContainer = styled(Box)(({ theme }) => ({
  minHeight: '80vh',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
  position: 'relative',
  background: `url('https://placehold.co/1200x600/FFF8DC/8B4513?text=Welcome+to+Sweets+You+Knead') center/cover no-repeat`,
  '&::before': {
    content: '""',
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: 'rgba(255, 248, 220, 0.7)',
  },
}));

const ContentBox = styled(Box)(({ theme }) => ({
  position: 'relative',
  zIndex: 1,
  textAlign: 'center',
  padding: theme.spacing(4),
  maxWidth: '800px',
}));

const Tagline = styled(Typography)(({ theme }) => ({
  fontFamily: '"Caveat", cursive',
  fontSize: '3rem',
  color: theme.palette.primary.main,
  marginBottom: theme.spacing(4),
  textShadow: '2px 2px 4px rgba(0,0,0,0.1)',
}));

const Description = styled(Typography)(({ theme }) => ({
  fontFamily: '"Quicksand", sans-serif',
  fontSize: '1.2rem',
  color: theme.palette.primary.dark,
  marginBottom: theme.spacing(4),
}));

const Hero: React.FC = () => {
  return (
    <HeroContainer>
      <ContentBox>
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
        >
          <Tagline variant="h1">
            Baked with heart, served with joy
          </Tagline>
          <Description>
            Welcome to our artisan bakery, where every treat is crafted with love and the finest ingredients.
            From our signature cupcakes to our freshly baked bread, each creation tells a story of passion and tradition.
          </Description>
        </motion.div>
      </ContentBox>
    </HeroContainer>
  );
};

export default Hero;
