import React from 'react';
import { Box, Container, Typography, Card, CardContent, CardMedia, Button, Chip } from '@mui/material';
import { styled } from '@mui/material/styles';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import { Link } from 'react-router-dom';

const MenuContainer = styled(Container)(({ theme }) => ({
  padding: theme.spacing(8, 0),
  position: 'relative',
}));

const MenuItemCard = styled(Card)(({ theme }) => ({
  height: '100%',
  display: 'flex',
  flexDirection: 'column',
  borderRadius: '15px',
  overflow: 'hidden',
  boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
  transition: 'transform 0.3s ease, box-shadow 0.3s ease',
  '&:hover': {
    transform: 'translateY(-5px)',
    boxShadow: '0 6px 12px rgba(0,0,0,0.15)',
  },
  '& .MuiCardMedia-root': {
    height: 250,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
  },
  '& .MuiCardContent-root': {
    flexGrow: 1,
    padding: theme.spacing(3),
    background: theme.palette.background.paper,
  }
}));

const CategoryTitle = styled(Typography)(({ theme }) => ({
  fontFamily: '"Caveat", cursive',
  fontSize: '2.5rem',
  color: theme.palette.primary.main,
  textAlign: 'center',
  marginBottom: theme.spacing(4),
  marginTop: theme.spacing(6),
  textShadow: '2px 2px 4px rgba(0,0,0,0.1)',
  '&:first-of-type': {
    marginTop: theme.spacing(2),
  }
}));

const menuCategories = {
  customCakes: {
    title: 'Custom Cakes',
    items: [
      {
        name: 'Galaxy Cake',
        description: 'Rich chocolate layers with chocolate ganache and fresh berries',
        image: 'images/IMG_2758.webp',
        price: '$65',
        tags: ['Chocolate', 'Ganache', 'Berries']
      },
      {
        name: 'Vanilla Bean Wedding Cake',
        description: 'Elegant vanilla bean cake with buttercream frosting and edible flowers',
        image: 'https://placehold.co/400x300/FFF5E6/8B4513?text=ðŸ°+Vanilla+Wedding',
        price: '$85',
        tags: ['Vanilla', 'Wedding', 'Buttercream']
      },
      {
        name: 'Red Velvet Celebration',
        description: 'Classic red velvet with cream cheese frosting and gold accents',
        image: 'https://placehold.co/400x300/FFF5E6/8B4513?text=ðŸ°+Red+Velvet',
        price: '$75',
        tags: ['Red Velvet', 'Cream Cheese', 'Celebration']
      },
      {
        name: 'Lemon Blueberry Delight',
        description: 'Light lemon cake with fresh blueberry filling and lemon glaze',
        image: 'https://placehold.co/400x300/FFF5E6/8B4513?text=ðŸ°+Lemon+Blueberry',
        price: '$70',
        tags: ['Lemon', 'Blueberry', 'Fresh']
      },
      {
        name: 'Chocolate Cake',
        description: 'Rich chocolate cake with chocolate ganache and fresh berries',
        image: 'images/IMG_2758.webp',
        price: '$65',
        tags: ['Chocolate', 'Ganache', 'Berries']
      }
    ]
  },
  customCupcakes: {
    title: 'Custom Cupcakes',
    items: [
      {
        name: 'Salted Caramel Cupcakes',
        description: 'Moist vanilla cupcakes with salted caramel frosting and caramel drizzle',
        image: 'https://placehold.co/400x300/FFF5E6/8B4513?text=ðŸ§+Salted+Caramel',
        price: '$28/dozen',
        tags: ['Caramel', 'Salted', 'Vanilla']
      },
      {
        name: 'Chocolate Mint Cupcakes',
        description: 'Chocolate cupcakes with mint buttercream and chocolate chips',
        image: 'https://placehold.co/400x300/FFF5E6/8B4513?text=ðŸ§+Chocolate+Mint',
        price: '$26/dozen',
        tags: ['Chocolate', 'Mint', 'Buttercream']
      },
      {
        name: 'Strawberry Cheesecake Cupcakes',
        description: 'Vanilla cupcakes with strawberry filling and cheesecake frosting',
        image: 'https://placehold.co/400x300/FFF5E6/8B4513?text=ðŸ§+Strawberry+Cheesecake',
        price: '$30/dozen',
        tags: ['Strawberry', 'Cheesecake', 'Filled']
      },
      {
        name: 'Cookies & Cream Cupcakes',
        description: 'Chocolate cupcakes with cookies & cream frosting and cookie crumbles',
        image: 'https://placehold.co/400x300/FFF5E6/8B4513?text=ðŸ§+Cookies+Cream',
        price: '$27/dozen',
        tags: ['Cookies', 'Cream', 'Chocolate']
      }
    ]
  },
  breadProducts: {
    title: 'Bread Products',
    items: [
      {
        name: 'Artisan Sourdough',
        description: 'Traditional sourdough bread with crispy crust and tangy flavor',
        image: 'https://placehold.co/400x300/FFF5E6/8B4513?text=ðŸž+Sourdough',
        price: '$8/loaf',
        tags: ['Sourdough', 'Artisan', 'Traditional']
      },
      {
        name: 'Cinnamon Raisin Bread',
        description: 'Sweet bread with plump raisins and cinnamon swirl',
        image: 'https://placehold.co/400x300/FFF5E6/8B4513?text=ðŸž+Cinnamon+Raisin',
        price: '$7/loaf',
        tags: ['Cinnamon', 'Raisin', 'Sweet']
      },
      {
        name: 'Whole Wheat Multigrain',
        description: 'Nutritious bread with seeds and whole grains',
        image: 'https://placehold.co/400x300/FFF5E6/8B4513?text=ðŸž+Multigrain',
        price: '$9/loaf',
        tags: ['Whole Wheat', 'Multigrain', 'Healthy']
      },
      {
        name: 'Brioche Rolls',
        description: 'Buttery, soft brioche rolls perfect for sandwiches',
        image: 'https://placehold.co/400x300/FFF5E6/8B4513?text=ðŸž+Brioche+Rolls',
        price: '$12/dozen',
        tags: ['Brioche', 'Buttery', 'Soft']
      }
    ]
  },
  seasonalItems: {
    title: 'Seasonal Items',
    items: [
      {
        name: 'Pumpkin Spice Cookies',
        description: 'Warm pumpkin spice cookies with maple glaze (Fall)',
        image: 'https://placehold.co/400x300/FFF5E6/8B4513?text=ðŸª+Pumpkin+Spice',
        price: '$18/dozen',
        tags: ['Pumpkin', 'Spice', 'Fall']
      },
      {
        name: 'Peppermint Brownies',
        description: 'Rich chocolate brownies with peppermint frosting (Winter)',
        image: 'https://placehold.co/400x300/FFF5E6/8B4513?text=ðŸ«+Peppermint+Brownies',
        price: '$24/dozen',
        tags: ['Peppermint', 'Chocolate', 'Winter']
      },
      {
        name: 'Strawberry Rhubarb Pie',
        description: 'Classic pie with sweet strawberries and tart rhubarb (Spring)',
        image: 'https://placehold.co/400x300/FFF5E6/8B4513?text=ðŸ¥§+Strawberry+Rhubarb',
        price: '$22/pie',
        tags: ['Strawberry', 'Rhubarb', 'Spring']
      },
      {
        name: 'Lemon Lavender Scones',
        description: 'Light scones with lemon zest and lavender (Summer)',
        image: 'https://placehold.co/400x300/FFF5E6/8B4513?text=ðŸ¥+Lemon+Lavender',
        price: '$20/dozen',
        tags: ['Lemon', 'Lavender', 'Summer']
      }
    ]
  }
};

const itemSliderSettings = {
  dots: false,
  infinite: false,
  speed: 500,
  slidesToShow: 4,
  slidesToScroll: 1,
  arrows: true,
  responsive: [
    {
      breakpoint: 1200,
      settings: {
        slidesToShow: 3,
      },
    },
    {
      breakpoint: 900,
      settings: {
        slidesToShow: 2,
      },
    },
    {
      breakpoint: 600,
      settings: {
        slidesToShow: 1,
      },
    },
  ],
};

const Menu: React.FC = () => {
  return (
    <MenuContainer>
      <Typography
        variant="h1"
        component="h1"
        align="center"
        sx={{
          fontFamily: '"Caveat", cursive',
          fontSize: '4rem',
          color: 'primary.main',
          marginBottom: 6,
          textShadow: '2px 2px 4px rgba(0,0,0,0.1)',
        }}
      >
        Our Menu
      </Typography>
      
      <Typography
        variant="body1"
        align="center"
        sx={{
          fontSize: '1.2rem',
          color: 'text.secondary',
          marginBottom: 6,
          maxWidth: '800px',
          mx: 'auto',
        }}
      >
        Discover our handcrafted treats made with love and the finest ingredients. 
        Each item is baked fresh daily to bring you the perfect taste of homemade goodness.
      </Typography>

      {Object.entries(menuCategories).map(([categoryKey, category]) => (
        <Box key={categoryKey} sx={{ mb: 8 }}>
          <CategoryTitle variant="h2">
            {category.title}
          </CategoryTitle>
          <Slider {...itemSliderSettings}>
            {category.items.map((item, index) => (
              <Box key={index} sx={{ px: 2 }}>
                <MenuItemCard>
                  <CardMedia
                    component="img"
                    image={item.image}
                    alt={item.name}
                  />
                  <CardContent>
                    <Typography
                      variant="h5"
                      component="h3"
                      sx={{
                        fontFamily: '"Caveat", cursive',
                        fontSize: '1.6rem',
                        color: 'primary.main',
                        marginBottom: 1,
                      }}
                    >
                      {item.name}
                    </Typography>
                    <Typography
                      variant="body2"
                      color="text.secondary"
                      sx={{ marginBottom: 2, minHeight: '60px' }}
                    >
                      {item.description}
                    </Typography>
                    <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 0.5, marginBottom: 2 }}>
                      {item.tags.map((tag, tagIndex) => (
                        <Chip
                          key={tagIndex}
                          label={tag}
                          size="small"
                          sx={{
                            backgroundColor: 'secondary.main',
                            color: 'white',
                            fontSize: '0.7rem',
                          }}
                        />
                      ))}
                    </Box>
                    <Typography
                      variant="h6"
                      color="secondary.main"
                      sx={{
                        fontFamily: '"Caveat", cursive',
                        fontSize: '1.4rem',
                        fontWeight: 'bold',
                      }}
                    >
                      {item.price}
                    </Typography>
                  </CardContent>
                </MenuItemCard>
              </Box>
            ))}
          </Slider>
        </Box>
      ))}

      <Box sx={{ textAlign: 'center', marginTop: 6 }}>
        <Typography
          variant="h4"
          sx={{
            fontFamily: '"Caveat", cursive',
            color: 'primary.main',
            marginBottom: 3,
          }}
        >
          Custom Orders Welcome!
        </Typography>
        <Typography
          variant="body1"
          sx={{ marginBottom: 4, maxWidth: '600px', mx: 'auto' }}
        >
          Don't see exactly what you're looking for? We love creating custom orders! 
          Contact us to discuss your special requirements and we'll make your vision come to life.
        </Typography>
        <Button
          component={Link}
          to="/#contact"
          variant="contained"
          color="primary"
          size="large"
          sx={{
            fontFamily: '"Caveat", cursive',
            fontSize: '1.5rem',
            padding: '12px 40px',
            borderRadius: '30px',
            backgroundColor: 'primary.main',
            textDecoration: 'none',
            '&:hover': {
              backgroundColor: 'primary.dark',
            },
          }}
        >
          Order Now
        </Button>
      </Box>
    </MenuContainer>
  );
};

export default Menu;
