import React from 'react';
import { Box, Typography, Paper } from '@mui/material';
import { styled } from '@mui/material/styles';
import { motion } from 'framer-motion';

const StoryContainer = styled(Box)(({ theme }) => ({
  padding: theme.spacing(8, 0),
  background: `url('/notebook-bg.png') repeat`,
  position: 'relative',
  '&::before': {
    content: '""',
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    background: 'rgba(255, 248, 220, 0.9)',
  },
}));

const StoryContent = styled(Paper)(({ theme }) => ({
  position: 'relative',
  padding: theme.spacing(6),
  background: '#fff',
  borderRadius: '8px',
  boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
  maxWidth: '800px',
  margin: '0 auto',
  '&::before': {
    content: '""',
    position: 'absolute',
    top: -20,
    left: -20,
    right: -20,
    bottom: -20,
    background: `url('/wheat-border.png') repeat`,
    zIndex: -1,
    opacity: 0.1,
  },
}));

const StoryTitle = styled(Typography)(({ theme }) => ({
  fontFamily: '"Caveat", cursive',
  fontSize: '2.5rem',
  color: theme.palette.primary.main,
  textAlign: 'center',
  marginBottom: theme.spacing(4),
  '&::before, &::after': {
    content: '""',
    display: 'inline-block',
    width: '40px',
    height: '40px',
    backgroundImage: 'url("/wheat-icon.png")',
    backgroundSize: 'contain',
    backgroundRepeat: 'no-repeat',
    margin: '0 15px',
    verticalAlign: 'middle',
  },
}));

const StoryText = styled(Typography)(({ theme }) => ({
  fontFamily: '"Quicksand", sans-serif',
  fontSize: '1.1rem',
  lineHeight: 1.8,
  color: theme.palette.text.primary,
  marginBottom: theme.spacing(3),
  textAlign: 'justify',
}));

const OurStory: React.FC = () => {
  return (
    <StoryContainer>
      <Box sx={{ position: 'relative', zIndex: 1 }}>
        <StoryContent>
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
          >
            <StoryTitle variant="h2">Our Story</StoryTitle>
            <StoryText>
            It all started in high school, selling brownies and cookies just to make a little extra cash. I never imagined it would turn into something bigger—until I read Finding Your Element by Sir Ken Robinson. That book lit a spark. I realized I wanted to take my creativity and shape it into something real, something people could taste.
            That book lit a spark. I realized I wanted to take my creativity and shape it into something real, something people could taste.
        
            </StoryText>
            <StoryText>
              Our journey began when Grandma's secret sourdough starter found its way into our modern kitchen. 
              That 100-year-old starter, passed down through generations, became the heart of our bakery. 
              Today, we continue to blend traditional techniques with innovative flavors, creating treats that tell stories of heritage and passion.
            </StoryText>
            <StoryText>
              Every cupcake, every loaf of bread, and every pastry that leaves our kitchen carries with it the warmth of our family's tradition 
              and the joy of creating something special for our community. We believe that baking is more than just making food – 
              it's about creating moments of happiness, one delicious bite at a time.
            </StoryText>
          </motion.div>
        </StoryContent>
      </Box>
    </StoryContainer>
  );
};

export default OurStory;
