import React from 'react';
import { Box, Typography, Grid, Paper } from '@mui/material';
import { styled } from '@mui/material/styles';
import { motion } from 'framer-motion';

const SectionTitle = styled(Typography)(({ theme }) => ({
  fontFamily: '"Caveat", cursive',
  fontSize: '2.5rem',
  color: theme.palette.primary.main,
  textAlign: 'center',
  marginBottom: theme.spacing(6),
  '&::after': {
    content: '""',
    display: 'block',
    width: '100px',
    height: '2px',
    backgroundColor: theme.palette.secondary.main,
    margin: '10px auto',
  },
}));

const ProductCard = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(2),
  background: '#fff',
  borderRadius: '8px',
  boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
  transition: 'transform 0.3s ease-in-out',
  '&:hover': {
    transform: 'translateY(-5px)',
  },
}));

const ProductImage = styled(Box)(({ theme }) => ({
  width: '100%',
  height: '200px',
  backgroundSize: 'cover',
  backgroundPosition: 'center',
  borderRadius: '4px',
  marginBottom: theme.spacing(2),
}));

const ProductTitle = styled(Typography)(({ theme }) => ({
  fontFamily: '"Caveat", cursive',
  fontSize: '1.8rem',
  color: theme.palette.primary.main,
  marginBottom: theme.spacing(1),
}));

const ProductDescription = styled(Typography)(({ theme }) => ({
  fontFamily: '"Quicksand", sans-serif',
  fontSize: '1rem',
  color: theme.palette.text.secondary,
}));

const products = [
  {
    id: 1,
    title: 'Classic Vanilla Cupcake',
    description: 'Moist vanilla cake topped with fluffy buttercream frosting',
    image: 'https://placehold.co/400x300/FFF8DC/8B4513?text=Vanilla+Cupcake',
  },
  {
    id: 2,
    title: 'Artisan Sourdough',
    description: 'Traditional sourdough bread made with our 100-year-old starter',
    image: 'https://placehold.co/400x300/FFF8DC/8B4513?text=Sourdough+Bread',
  },
  {
    id: 3,
    title: 'Chocolate Dream',
    description: 'Rich chocolate cake with ganache and chocolate shavings',
    image: 'https://placehold.co/400x300/FFF8DC/8B4513?text=Chocolate+Cake',
  },
  {
    id: 4,
    title: 'Cinnamon Roll',
    description: 'Freshly baked cinnamon rolls with cream cheese frosting',
    image: 'https://placehold.co/400x300/FFF8DC/8B4513?text=Cinnamon+Roll',
  },
];

const MenuPreview: React.FC = () => {
  return (
    <Box sx={{ py: 8 }}>
      <SectionTitle variant="h2">Our Delicious Creations</SectionTitle>
      <Grid container spacing={4}>
        {products.map((product) => (
          <Grid item xs={12} sm={6} md={3} key={product.id}>
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5 }}
              viewport={{ once: true }}
            >
              <ProductCard>
                <ProductImage
                  sx={{
                    backgroundImage: `url(${product.image})`,
                  }}
                />
                <ProductTitle>{product.title}</ProductTitle>
                <ProductDescription>{product.description}</ProductDescription>
              </ProductCard>
            </motion.div>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

export default MenuPreview;
