        image: 'https://placehold.co/400x300/FFF5E6/8B4513?text=ðŸ¥+Lemon+Lavender',
        price: '$20/dozen',
        tags: ['Lemon', 'Lavender', 'Summer']
      }
    ]
  }
};

const itemSliderSettings = {
  dots: false,
  infinite: false,
  speed: 500,
  slidesToShow: 4,
  slidesToScroll: 1,
  arrows: true,
  responsive: [
    {
      breakpoint: 1200,
      settings: {
        slidesToShow: 3,
      },
    },
    {
      breakpoint: 900,
      settings: {
        slidesToShow: 2,
      },
    },
    {
      breakpoint: 600,
      settings: {
        slidesToShow: 1,
      },
    },
  ],
};

const Menu: React.FC = () => {
  const [enlarged, setEnlarged] = useState<string | null>(null);

  const handleImgClick = (key: string) => {
    setEnlarged(prev => (prev === key ? null : key));
  };

  return (
    <MenuContainer>
      {/* Custom slick arrow styles */}
      <style>{`
        .slick-arrow {
          z-index: 2;
          display: flex !important;
          align-items: center;
          justify-content: center;
        }
        .slick-prev, .slick-next {
          width: 48px;
          height: 48px;
          background: transparent;
          color: #8B4513 !important;
          transition: color 0.2s;
          border: none;
          position: absolute;
          top: 50%;
          transform: translateY(-50%);
        }
        .slick-prev {
          left: -60px;
        }
        .slick-next {
          right: -60px;
        }
        .slick-prev:before, .slick-next:before {
          font-size: 2rem;
          color: #8B4513;
          opacity: 1;
          display: flex;
          align-items: center;
          justify-content: center;
          line-height: 1;
          width: 100%;
          height: 100%;
          margin: 0;
          padding: 0;
        }
        .slick-prev:hover, .slick-next:hover {
          color: #5a2d0c !important;
        }
        .slick-prev:hover:before, .slick-next:hover:before {
          color: #5a2d0c;
        }
        /* Image zoom on hover */
        .MuiCardMedia-root {
          transition: transform 0.35s cubic-bezier(.4,2,.6,1);
          cursor: pointer;
        }
        .MuiCard-root:hover .MuiCardMedia-root {
          transform: scale(1.08);
        }
        /* Image zoom on click */
        .enlarged-img {
          z-index: 10;
          position: relative;
          transform: scale(2.2);
          box-shadow: 0 8px 32px rgba(0,0,0,0.18);
          border-radius: 18px;
          transition: transform 0.35s cubic-bezier(.4,2,.6,1), box-shadow 0.2s;
        }
      `}</style>
      <Typography
        variant="h1"
        component="h1"
        align="center"
        sx={{
          fontFamily: '"Caveat", cursive',
          fontSize: '4rem',
          color: 'primary.main',
          marginBottom: 6,
          textShadow: '2px 2px 4px rgba(0,0,0,0.1)',
        }}
      >
        Our Menu
      </Typography>
      
      <Typography
        variant="body1"
        align="center"
        sx={{
          fontSize: '1.2rem',
          color: 'text.secondary',
          marginBottom: 6,
          maxWidth: '800px',
          mx: 'auto',
        }}
      >
        Discover our handcrafted treats made with love and the finest ingredients. 
        Each item is baked fresh daily to bring you the perfect taste of homemade goodness.
      </Typography>

      {Object.entries(menuCategories).map(([categoryKey, category]) => (
        <Box key={categoryKey} sx={{ mb: 8 }}>
          <CategoryTitle variant="h2">
            {category.title}
          </CategoryTitle>
          <Slider {...itemSliderSettings}>
            {category.items.map((item, index) => {
              const imgKey = `${categoryKey}-${index}`;
              return (
                <Box key={index} sx={{ px: 2 }}>
                  <MenuItemCard>
                    <CardMedia
                      component="img"
                      image={item.image}
                      alt={item.name}
                      className={enlarged === imgKey ? 'enlarged-img' : ''}
                      onClick={() => handleImgClick(imgKey)}
                    />
                    <CardContent>
                      <Typography
                        variant="h5"
                        component="h3"
                        sx={{
                          fontFamily: '"Caveat", cursive',
                          fontSize: '1.6rem',
                          color: 'primary.main',
                          marginBottom: 1,
                        }}
                      >
                        {item.name}
                      </Typography>
                      <Typography
                        variant="body2"
                        color="text.secondary"
                        sx={{ marginBottom: 2, minHeight: '60px' }}
                      >
                        {item.description}
                      </Typography>
                      <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 0.5, marginBottom: 2 }}>
                        {item.tags.map((tag, tagIndex) => (
                          <Chip
                            key={tagIndex}
                            label={tag}
                            size="small"
                            sx={{
                              backgroundColor: 'secondary.main',
                              color: 'white',
                              fontSize: '0.7rem',
                            }}
                          />
                        ))}
                      </Box>
                      <Typography
                        variant="h6"
                        color="secondary.main"
                        sx={{
                          fontFamily: '"Caveat", cursive',
                          fontSize: '1.4rem',
                          fontWeight: 'bold',
                        }}
                      >
                        {item.price}
                      </Typography>
                    </CardContent>
                  </MenuItemCard>
                </Box>
              );
            })}
          </Slider>
        </Box>
      ))}

      <Box sx={{ textAlign: 'center', marginTop: 6 }}>
        <Typography
          variant="h4"
          sx={{
            fontFamily: '"Caveat", cursive',
            color: 'primary.main',
            marginBottom: 3,
          }}
        >
          Custom Orders Welcome!
        </Typography>
        <Typography
          variant="body1"
          sx={{ marginBottom: 4, maxWidth: '600px', mx: 'auto' }}
        >
          Don't see exactly what you're looking for? We love creating custom orders! 
          Contact us to discuss your special requirements and we'll make your vision come to life.
        </Typography>
        <Button
          component={Link}
          to="/#contact"
          variant="contained"
          color="primary"
          size="large"
          sx={{
            fontFamily: '"Caveat", cursive',
            fontSize: '1.5rem',
            padding: '12px 40px',
            borderRadius: '30px',
            backgroundColor: 'primary.main',
            textDecoration: 'none',
            '&:hover': {
              backgroundColor: 'primary.dark',
            },
          }}
        >
          Order Now
        </Button>
      </Box>
    </MenuContainer>
  );
};

export default Menu;
